export class Answer {
  constructor(question, answer, isAnswered) {
    this.question = question;
    this.answer = answer;
    this.isAnswered = isAnswered;
  }

  setAnswer(answer) {
    if (answer) {
      this.answer = answer;
      this.isAnswered = true;
    }
  }

  removeAnswer() {
    this.answer = '';
    this.isAnswered = false;
  }

}
